	 function sendGDPFAgreement(isAccept) {
	   $.ajax({
			type: "POST",
			dataType: 'json', 
			url: "/employees/gdprAgrement",	
			async: true,
			data :'isAccept='+isAccept,
			cache: false,
			beforeSend : function()  {
				$('#loadingImg').removeClass('displayNone');
				 $('#loadingImg').show();	
			},
			success: function(response){ 
				var acceptTerms = (isAccept != 0 ? 'Accepted' : 'Not Accepted');
				$('#loadingImg').addClass('displayNone');
				$('#loadingImg').hide();	
				$('.gdprAgreement').hide();				 
				$('#gdprDialog').dialog('close');
				$('.ui-widget-overlay-gdpr').addClass('clear'); 
				$('#successAlert').removeClass('clear');
				$('#successAlertMsg').html('You have '+acceptTerms+' the request');
				$('#successAlert').fadeIn();
				$('#successAlert').removeClass('clear');
				$('html, body').animate({ scrollTop: 1 }, 500);
				setTimeout(function(){$('#successAlert').fadeOut("slow");}, 5000);
				showidfForm();
			}
		}); 
   }

   function sendCIDForm(strCidf){
		$.ajax({
			type: "POST",
			dataType: 'json', 
			url: "/dashboard/savecidform",	
			async: true,
			data : strCidf,
			cache: false,
			beforeSend : function()  {
				$('#loadingImg').removeClass('displayNone');
				$('#loadingImg').show();	
			},
			success: function(response){ 

				$('#loadingImg').addClass('displayNone');
				$('#loadingImg').hide();	
				$('.cidfForm').hide();				 
				$('#cidfDialog').dialog('close');
				$('.ui-widget-overlay-cidf').addClass('clear'); 
				$('#successAlert').removeClass('clear');
				$('#successAlertMsg').html('You have submitted Interest Disclosure Form Successfully.');
				$('#successAlert').fadeIn();
				$('#successAlert').removeClass('clear');
				$('html, body').animate({ scrollTop: 1 }, 500);
				setTimeout(function(){$('#successAlert').fadeOut("slow");}, 5000);
				showHappyDialog();
			}
		});
	}

