$(document).on('mouseover', 'div.starWrapper i',function(){
	$("div.starWrapper i.fa-star-o").css('color','#FFD119');
	var rating = $(this).data("value");
	var maxVal=$('.clickStarVal').val();
	for (i = 0; i <=rating; i++) { 
		$("i.feedStar"+i).removeClass("fa-star-o");
		$("i.feedStar"+i).addClass("fa-star");
	}
});
$(document).on('mouseout', 'div.starWrapper i',function(){
	var rating = $(this).data("value");
	var maxVal=$('.clickStarVal').val();
	if(maxVal.length > 0){
	for (i = (parseInt(maxVal)+1); i <= 5; i++) {   
		$("i.feedStar"+i).addClass("fa-star-o");
		$("i.feedStar"+i).removeClass("fa-star");
	}
	}else{
		$("div.starWrapper i").addClass("fa-star-o");
		$("div.starWrapper i").removeClass("fa-star");
	}
});


$(document).on('click ', 'div.starWrapper i',function(){
	var rating = $(this).data("value");
	$('.clickStarVal').val(rating);
	$("div.starWrapper i.fa-star").addClass("fa-star-o");
	$("div.starWrapper i.fa-star").removeClass("fa-star");
	for (i = 0; i <= rating; i++) { 
		$("i.feedStar"+i).removeClass("fa-star-o");
		$("i.feedStar"+i).addClass("fa-star");
	}
});
$(document).on(' keypress',  '.ui-autocomplete-input' , function(event){ 
    var keycode = (event.keyCode ? event.keyCode : event.which);
    if(keycode == '13'){ 
       getEmpDtls();
    }
});

	$(document).on('click ', '.ui-menu-item',function(){
		getEmpDtls();
    });

	function getEmpDtls(){

		var id = $('#searchKey').val();

         if (id.replace(/^\s+|\s+$/g, '').length > 0) {
            $(this).attr("target", "_blank");			
			if($('#customsearch').val()=='Employee'){
			url='/employees/bio?EmpId=' + id;			
			location.href = url;
		  }

          }
			var id = $('#searchKey').val();
				if (id.replace(/^\s+|\s+$/g, '').length > 0) {
					$(this).attr("target", "_blank");
				if($('#customsearch').val()=='Skills'){
					url='/employees/skills?id=' + id;
					location.href = url;
		  }
		  }
		 var hobid = $('#searchKey').val();
			  if (hobid.replace(/^\s+|\s+$/g, '').length > 0) {
				$(this).attr("target", "_blank");
			 if($('#customsearch').val()=='Hobbies'){
				url='/employees/hobbies?hobby_id=' + hobid +'&search=hobby';
				location.href = url;
		  }
		  }

	}

$("#attr_reason").addClass('form-control');

function gobalCombo(){
		$("#searchKey").val("");
		$("#searchKey").next('input').val("");
		$('.keysearch').attr("placeholder",$('#customsearch').val());
			$('#searchKey').next('input').attr('title','');
		if($('#customsearch').val()=='Skills'){
			$('#searchKey').ajaxcombobox({src: "http://10.11.12.141:98/employees/searchdtls"});
			$('#infoFormat').css('visibility','hidden');
	   }
	   if($('#customsearch').val()=='Hobbies'){
			$('#searchKey').ajaxcombobox({src: "http://10.11.12.141:98/employees/getdepts"});
			$('#infoFormat').css('visibility','hidden');
	   }
	    if($('#customsearch').val()=='Employee'){
		$('#searchKey').next('input').attr('title', "Search Format : <<FirstName MiddleName LastName>>");
		$('#searchKey').ajaxcombobox({src: "http://10.11.12.141:98/employees/search"});
		$('#infoFormat').css('visibility','visible');
	   }

}

